def count_all(entry):
    count = 0
    for x in entry:
        if x == "(":
            count = count + 1
        elif x == ")":
            count = count - 1
    return count
    
def first_basement_position(entry):
    position = 0
    floor = 0
    for x in entry:
        position += 1
        if x == "(":
            floor += 1
        elif x == ")":
            floor -= 1
        if floor == -1:
            return position
    return None

